mapper.select:
SELECT B.* FROM ( SELECT A.*, ROW_NUMBER() OVER(ORDER BY GROUPSEQ DESC) AS RNUM FROM COVERLETTER A WHERE CVCATEGORY = #{cvcategory} AND JOINEMAIL = #{joinemail}
AND TITLE LIKE ('%' || #{keyword} || '%')
AND CONTENT LIKE ('%' || #{keyword} || '%')
AND JOINEMAIL LIKE ('%' || #{keyword} || '%')
) B WHERE RNUM BETWEEN #{StartIndex} + 1 AND #{CntPerPage} ORDER BY GROUPSEQ DESC

